import { Component, EventEmitter, Output } from '@angular/core';

@Component({
  selector: 'app-campaign-filter',
  templateUrl: './campaign-filter.component.html',
  styleUrls: ['./campaign-filter.component.css']
})
export class CampaignFilterComponent {
  texto = '';

  @Output() filtrar = new EventEmitter<string>();
  @Output() limpar = new EventEmitter<void>();

  emitirFiltro(): void {
    this.filtrar.emit(this.texto);
  }

  limparFiltro(): void {
    this.texto = '';
    this.limpar.emit();
  }
}
<div class="card filtro">
  <div class="linha">
    <input
      [(ngModel)]="texto"
      (keyup.enter)="emitirFiltro()"
      placeholder="Filtrar por título (Enter para buscar)"
      class="input" />

    <button class="btn btn-outline" (click)="emitirFiltro()">Buscar</button>
    <button class="btn" (click)="limparFiltro()">Limpar</button>
  </div>

  <p class="hint">
    Dica: esse componente é “filho” e emite eventos para o “pai” via <b>{{ '@Output' }}</b>.
  </p>
</div>
