import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Campaign } from '../../models/campaign.model';
import { CampanhasService } from '../../services/campanhas.service';

@Component({
  selector: 'app-campanha-detalhe',
  templateUrl: './campanha-detalhe.component.html',
  styleUrls: ['./campanha-detalhe.component.css']
})
export class CampanhaDetalheComponent implements OnInit {
  campanha?: Campaign;
  id?: number;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private campanhasService: CampanhasService
  ) {}

  ngOnInit(): void {
    const idStr = this.route.snapshot.paramMap.get('id');
    this.id = idStr ? Number(idStr) : undefined;

    if (!this.id || Number.isNaN(this.id)) {
      this.router.navigate(['/campanhas']);
      return;
    }

    this.campanha = this.campanhasService.getById(this.id);
  }
}
<div class="card" *ngIf="campanha; else naoEncontrada">
  <h2>{{ campanha.titulo }}</h2>

  <p>
    <span class="badge"
      [ngClass]="{ 'badge-ativo': campanha.ativo, 'badge-inativo': !campanha.ativo }">
      {{ campanha.ativo ? 'ATIVA' : 'INATIVA' }}
    </span>
  </p>

  <p><b>Descrição:</b> {{ campanha.descricao }}</p>
  <p class="muted"><b>Criada em:</b> {{ campanha.criadoEm | date:'dd/MM/yyyy' }}</p>

  <div class="acoes">
    <a class="btn btn-outline" routerLink="/campanhas">Voltar</a>
  </div>
</div>

<ng-template #naoEncontrada>
  <div class="card">
    <h3>Campanha não encontrada</h3>
    <p class="muted">
      Não existe campanha com id {{ id }} no mock atual.
    </p>
    <a class="btn" routerLink="/campanhas">Voltar para Campanhas</a>
  </div>
</ng-template>
