import { Component, OnInit } from '@angular/core';
import { Campaign } from '../../models/campaign.model';
import { CampanhasService } from '../../services/campanhas.service';

@Component({
  selector: 'app-campanhas',
  templateUrl: './campanhas.component.html',
  styleUrls: ['./campanhas.component.css']
})
export class CampanhasComponent implements OnInit {
  campanhas: Campaign[] = [];
  filtroAtual = '';

  constructor(private campanhasService: CampanhasService) {}

  ngOnInit(): void {
    this.recarregar();
  }

  recarregar(): void {
    this.campanhas = this.campanhasService.getAll();
    if (this.filtroAtual.trim()) {
      this.aplicarFiltro(this.filtroAtual);
    }
  }

  aplicarFiltro(texto: string): void {
    this.filtroAtual = texto;
    const t = texto.trim().toLowerCase();
    this.campanhas = this.campanhasService
      .getAll()
      .filter(c => c.titulo.toLowerCase().includes(t));
  }

  limparFiltro(): void {
    this.filtroAtual = '';
    this.campanhas = this.campanhasService.getAll();
  }

  remover(id: number): void {
    this.campanhasService.delete(id);
    this.recarregar();
  }

  trackById(index: number, item: Campaign): number {
    return item.id;
  }
}
<div class="topo-pagina">
  <h2>Campanhas</h2>
  <p class="muted">Exemplo completo com: *ngIf/else, *ngFor, trackBy, ngClass, Pipe e Input/Output.</p>
</div>

<app-campaign-filter
  (filtrar)="aplicarFiltro($event)"
  (limpar)="limparFiltro()">
</app-campaign-filter>

<!-- ngIf com else -->
<div *ngIf="campanhas.length > 0; else vazio">
  <div class="grid">
    <app-campaign-card
      *ngFor="let campanha of campanhas; trackBy: trackById"
      [campanha]="campanha"
      (remover)="remover($event)">
    </app-campaign-card>
  </div>
</div>

<ng-template #vazio>
  <div class="card vazio">
    <h3>Nenhuma campanha encontrada</h3>
    <p class="muted">
      Você pode limpar o filtro ou cadastrar novas campanhas (nas próximas aulas).
    </p>
    <button class="btn" (click)="limparFiltro()">Limpar filtro</button>
  </div>
</ng-template>
